type Mutation {
  add(x: Int!, y: Int!): Int!
}

type Query {
    """ Method to substract two integers """
    subtract(
        " First integer "
        x: Int,
        " Second integer "
        y: Int
    ): Int
}

enum Permission {
  ADMIN
  USER
}

scalar Date

type User {
  id: ID!
  username: String
  email: String!
  dateJoined: Date
  role: Permission
}

type Campaign {
  id: ID!
  title: String!
  description: String
  owner: User!
  createdAt: Date
  updatedAt: Date
}
